mapper.update:
UPDATE allQnA
SET   title = #{title}
  , content = #{content}
  , writer = #{writer}
  , regDate = now()
WHERE allqnaNo = #{allqnaNo}

mapper.select:
SELECT allqnaCmtNum, allqnaCmtNo, allqnaNo, cmtWriter, cmtContent, regDate, firstIdNo, modifyDate, lastIdNo
FROM allqnacmt
WHERE allqnaNo = #{allqnaNo}
AND allqnaCmtReplyNO is NULL;

mapper.update:
UPDATE allqnacmt
SET cmtContent = #{cmtContent}
  , cmtWriter = #{cmtWriter}
  , modifyDate = now()
WHERE allqnaNo = #{allqnaNo}
AND allqnaCmtNo = #{allqnaCmtNo}

mapper.delete:
DELETE
FROM   allqnacmt
WHERE allqnaNo = #{allqnaNo}
AND allqnaCmtNo = #{allqnaCmtNo}
AND cmtWriter = #{cmtWriter}

mapper.update:
UPDATE allqnacmt
SET cmtContent = #{cmtContent}
  , cmtWriter = #{cmtWriter}
  , modifyDate = now()
WHERE allqnaCmtNo = #{allqnaCmtNo}
  AND allqnaCmtReplyNo = #{allqnaCmtReplyNo}

mapper.delete:
DELETE
FROM   allqnacmt
WHERE allqnaCmtNo = #{allqnaCmtNo}
  AND allqnaCmtReplyNo = #{allqnaCmtReplyNo}

mapper.update:
UPDATE allQnA
SET   hit = hit + 1
WHERE allqnaNo = #{allqnaNo}
